
export interface Incident {
  /**
   * @displayName Incident
   * @typeHint uuid
   * @validationRule Required for create/update operations
   * @description Unique system identifier - GUID generated by system, immutable, used for relationships and internal references.
   * @uiConstraints NEVER create input fields, forms, or any UI elements that allow users to enter, edit, or manually input this ID. NEVER display this raw ID value to users in any UI component, table, card, or view. Users should NEVER see or type this value.
   * @userFacing This field must not appear in any user-facing interface
   * @inputProhibited true - Never create input fields for this value
   * @displayProhibited true - Never display this value to users
   * @internalOnly true - Use only for system operations like database queries, routing, API calls
   */
  id: string;
  /**
   * @displayName Incident Name
   * @format Text
   * @maxLength 850
   * @validationRule Required for create/update operations
   * @description Human-readable record identifier - user-editable display name shown in lookups, views, forms, used for searching and filtering.
   */
  incidentName: string;
  /**
   * @displayName DateAndTime
   * @format DateAndTime
   * @description DateTime format (YYYY-MM-DDTHH:mm). Display with date-fns: const formatted = format(new Date(value), "PPpp"); Avoid parseISO() since datetime-local values lack timezone info.
   */
  dateAndTime?: string;
  /**
   * @displayName Location
   * @format Text
   * @maxLength 100
   */
  location?: string;
  /**
   * @displayName Scene Diagram Url
   * @format MultiLine
   * @maxLength 2000
   */
  sceneDiagramUrl?: string;
}